import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const modules = {
  toolbar: [
    [{ 'header': [1, 2, false] }],
    ['bold', 'italic', 'underline', 'strike', 'blockquote'],
    [{ 'list': 'ordered' }, { 'list': 'bullet' }, { 'indent': '-1' }, { 'indent': '+1' }],
    ['link', 'image'],
    ['clean']
  ],
};

const formats = [
  'header',
  'bold', 'italic', 'underline', 'strike', 'blockquote',
  'list', 'bullet', 'indent',
  'link', 'image'
];

const CreatePost = () => {
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
  const [files, setFiles] = useState(null);

  const createNewPost = async (ev) => {
    ev.preventDefault();

    const data = new FormData();
    data.append("title", title);
    data.append("summary", summary);
    data.append("content", content);
    if (files) {
      data.append('file', files[0]);
    }
    data.append('password', localStorage.getItem("token"));

    try {
      const response = await fetch('http://localhost:4000/post', {
        method: 'POST',
        body: data,
        credentials: "include",
      });

      if (response.ok) {
        // Handle success, maybe redirect or show a success message
        console.log('Post created successfully!');
      } else {
        // Handle error
        console.error('Error creating post:', response.status);
      }
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  return (
    <form onSubmit={createNewPost}>
      <input type='text' placeholder='Title' value={title} onChange={(ev) => setTitle(ev.target.value)} />
      <input type='text' placeholder='Summary' value={summary} onChange={(ev) => setSummary(ev.target.value)} />
      <input type='file' onChange={(ev) => setFiles(ev.target.files)} />
      <ReactQuill value={content} onChange={(newValue) => setContent(newValue)} modules={modules} formats={formats} />
      <button style={{ marginTop: "10px" }} type="submit">Create Post</button>
    </form>
  );
};

export default CreatePost;
